mapper.update:
update business_place_image_path
set is_main = 'N'
where email = #{email}
and business_regi_num = #{business_regi_num}
and is_main = 'Y'

UPDATE business_place_image_path
  SET is_main = 'Y'
  WHERE email = #{email}
  AND business_regi_num = #{business_regi_num}
  AND place_img_path = #{place_img_path}

mapper.select:
select count(*)
from business_place_image_path
where email = #{email}
and business_regi_num = #{business_regi_num}
and is_main = 'Y'

select * 
from business_place_image_path
where email = #{email}
and business_regi_num = #{business_regi_num}
and is_main = 'Y'

mapper.update:
update business_place_image_path
set is_main = 'Y'
where email = #{email}
and business_regi_num = #{business_regi_num}
and place_img_path = (
    select place_img_path
    from business_place_image_path
    where email = #{email}
    and business_regi_num = #{business_regi_num}
    fetch first 1 rows only
)

mapper.insert:
insert into business_place_image_path (
	email, business_regi_num, place_img_path
) values (
    #{email}, #{business_regi_num}, #{place_img_path}
)

mapper.select:
select email, business_regi_num, place_img_path
    from business_place_image_path 
    where email=#{email} and business_regi_num=#{business_regi_num}
