mapper.insert:
insert into business_place_info (
    email, business_regi_num, place_info
) values (
    #{email}, #{business_regi_num}, #{place_info}
)

mapper.update:
update business_place_info
set place_info = #{place_info}
where 
    email = #{email} 
    and business_regi_num = #{business_regi_num}

mapper.select:
select email, business_regi_num, place_info
    from business_place_info 
    where email=#{email} and business_regi_num=#{business_regi_num}
